mapper.select:
select a_id, a_name, a_price, a_time, a_number, a_remark
from f_takeout_appointment
where delete_status = 1;

mapper.update:
update f_takeout_appointment
set a_name   = #{fTakeoutAppointment.aName},
    a_price  = #{fTakeoutAppointment.aPrice},
    a_time   = #{fTakeoutAppointment.aTime},
    a_number = #{fTakeoutAppointment.aNumber},
    a_remark = #{fTakeoutAppointment.aRemark}
where a_id = #{fTakeoutAppointment.aId}
  and delete_status = 1;

update f_takeout_appointment
set delete_status = 0
where a_id = #{aId}
  and delete_status = 1;
