mapper.select:
select c_id, c_name, c_type, c_img, c_price
from f_takeout_commodity
where delete_status = 1;

mapper.insert:
insert into f_takeout_commodity(c_name, c_type, c_img, c_price)
values (#{fTakeoutCommodityVo.cName},#{fTakeoutCommodityVo.cType},
#{fTakeoutCommodityVo.cImg},#{fTakeoutCommodityVo.cPrice});

mapper.update:
update f_takeout_commodity
set c_name = #{fTakeoutCommodity.cName},c_type = #{fTakeoutCommodity.cType},
c_img = #{fTakeoutCommodity.cImg},c_price = #{fTakeoutCommodity.cPrice}
where c_id = #{fTakeoutCommodity.cId}
and delete_status = 1;

update f_takeout_commodity
set delete_status = 0
where c_id = #{cId}
and delete_status = 1;
