mapper.select:
select t_id, t_name, t_sort, t_state
from f_takeout_type
where delete_status = 1;

mapper.update:
update f_takeout_type
set t_name  = #{fTakeoutType.tName},
    t_sort  = #{fTakeoutType.tSort},
    t_state = #{fTakeoutType.tState}
where t_id = #{fTakeoutType.tId}
  and delete_status = 1;

update f_takeout_type
set delete_status = 0
where t_id = #{tId}
  and delete_status = 1;
