    a.a_pass        = #{aPass},
    a.a_pass_reason = #{aPassReason},
    s.s_status      = 1
where a.s_id = #{sId}
  and s.s_id = #{sId}
  and a.a_type = 4
  and a.a_status = 2
  and s.delete_status = 1;

update f_audit a,f_shop s
set a.a_status      = 1,
    a.a_pass        = #{aPass},
    a.a_pass_reason = #{aPassReason},
    s.delete_status = 0
where a.s_id = #{sId}
  and s.s_id = #{sId}
  and a.a_type = 6
  and a.a_status = 2
  and s.delete_status = 1;
