mapper.select:
select o.o_id, o_discribe, o_no, o_createtime, o_type, o_price, u_id, o_diliver,
wp.p_id, p_name, p_describe, p_shop, p_tag, onsale_time, shelve_time, p_price, p_classification, p_diliver, p_details
from w_order o left join w_order_product wop on o.o_id = wop.o_id left join w_product wp on wop.p_id = wp.p_id


    o.o_id in (select o.o_id from w_order_product where wp.p_id=#{pId})


    and o.status=#{status}


    and o.o_type=#{oType}
